	"id" serial PRIMARY KEY NOT NULL,
	"name" text
);

CREATE TABLE IF NOT EXISTS "users" (
	"id" serial PRIMARY KEY NOT NULL,
	"name" text
);

CREATE TABLE IF NOT EXISTS "usersToChatGroups" (
	"user_id" integer NOT NULL,
	"group_id" integer NOT NULL
);

DO $$ BEGIN
 ALTER TABLE usersToChatGroups ADD CONSTRAINT usersToChatGroups_user_id_users_id_fk FOREIGN KEY ("user_id") REFERENCES users("id") ON DELETE no action ON UPDATE no action;
EXCEPTION
 WHEN duplicate_object THEN null;
END $$;

DO $$ BEGIN
 ALTER TABLE usersToChatGroups ADD CONSTRAINT usersToChatGroups_group_id_chat_groups_id_fk FOREIGN KEY ("group_id") REFERENCES chat_groups("id") ON DELETE no action ON UPDATE no action;
EXCEPTION
 WHEN duplicate_object THEN null;
END $$;
```

When executed in Postgres, the following error is thrown:
`ERROR: relation "userstochatgroups" does not exist`

This is to be expected because if the table name is not in quotes, it gets treated as case insensitive.

Ideally the query would look something like this:

```sql
CREATE TABLE IF NOT EXISTS "chat_groups" (
	"id" serial PRIMARY KEY NOT NULL,
	"name" text
);

CREATE TABLE IF NOT EXISTS "users" (
	"id" serial PRIMARY KEY NOT NULL,
	"name" text
);

CREATE TABLE IF NOT EXISTS "usersToChatGroups" (
	"user_id" integer NOT NULL,
	"group_id" integer NOT NULL
);

DO $$ BEGIN
 ALTER TABLE "usersToChatGroups" ADD CONSTRAINT usersToChatGroups_user_id_users_id_fk FOREIGN KEY ("user_id") REFERENCES users("id") ON DELETE no action ON UPDATE no action;
EXCEPTION
 WHEN duplicate_object THEN null;
END $$;

DO $$ BEGIN
 ALTER TABLE "usersToChatGroups" ADD CONSTRAINT usersToChatGroups_group_id_chat_groups_id_fk FOREIGN KEY ("group_id") REFERENCES chat_groups("id") ON DELETE no action ON UPDATE no action;
EXCEPTION
 WHEN duplicate_object THEN null;
END $$;
```

